import { Navbar } from "../layout"
import React, {useState} from 'react'
import './learn.css'

function Learn() {
    const [subject, setSubject] = useState("maths")

    const renderContent = () => {
        switch (subject) {
            case "physics":
                return <h2>Welcome to Physics</h2>
            case "maths":
                return <h2>Welcome to Mathematics</h2>
            case "chemistry":
                return <h2>Welcome to Chemistry</h2>
        }
    }

    const returnSubjectColor = () => {
        switch (subject) {
            case "physics":
                return 'red';
            case "maths":
                return 'red'
            case "chemistry":
                return 'green'
        }
    }

    return (<>
        <Navbar />
        <div className="subjectSelector">
            <button className="physicsButton" value={subject} onClick={() => setSubject("physics")}>Physics</button>
            <button className="mathsButton" value={subject} onClick={() => setSubject("maths")}>Mathematics</button>
            <button className="chemistryButton" value={subject} onClick={() => setSubject("chemistry")}>Chemistry</button>
        </div>
        <div className="subjectContent">
            {renderContent()}
        </div>
    </>)
}

export default Learn
